import React, {useRef} from 'react';
import { StyleSheet, Text } from 'react-native';
import Menu, { MenuItem, MenuDivider } from 'react-native-material-menu';
import AppContainer from './AppContainer';

export default function SignalementTabComponent() {
  const menu = useRef<Menu>(null);

    const hideMenu = () => {
      if (menu.current) {
        menu.current.hide();
      } else {
        throw new Error("Menu not found");
      }
    }

    const showMenu = () => {
      if (menu.current) {
        menu.current.show();
      } else {
        throw new Error("Menu not found");
      }
    } 

    return (
      <AppContainer>
        <Text> Welcome in the signalement !
        </Text>
        <Menu ref={menu} button={<Text onPress={showMenu}>Show menu</Text>}>
          <MenuItem style={styles.evenMenuItem} onPress={hideMenu}>15 min</MenuItem>
          <MenuItem style={styles.oddMenuItem} onPress={hideMenu}>30 min</MenuItem>
          <MenuItem style={styles.evenMenuItem} onPress={hideMenu}>45 min</MenuItem>
          {/* <MenuDivider /> */}
          <MenuItem style={styles.absenceItem} onPress={hideMenu}>Absence</MenuItem>
        </Menu>
      </AppContainer>
    )
}


const styles = StyleSheet.create({
  evenMenuItem: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'grey'
  },
  oddMenuItem: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  absenceItem: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    color: "#FF0000",
    fontSize: 30,
    fontWeight: "bold"  }
});
